document.addEventListener("DOMContentLoaded",function(){console.log("popup_load");const e=document.querySelectorAll('a[href*="#popup:"]');function o(){console.log("popup_open");var e=this.href.split("#popup:").reverse()[0];document.querySelector("#popup ."+e).classList.add("show"),document.querySelector("body").classList.add("show_popup")}for(i=0;i<e.length;i++)e[i].addEventListener("click",o);const t=document.querySelector(".close");function c(){console.log("popup_close"),document.querySelector("body").classList.remove("show_popup"),t.closest("#popup").querySelector(".popup_content.show").classList.remove("show"),console.log(t.closest("#popup").querySelector(".popup_content").classList)}t.addEventListener("click",c);const p=document.querySelector("#popup"),s=p.querySelector(".popup_container");p.addEventListener("click",e=>{e.composedPath().includes(s)||c()})});